import jwt from 'jsonwebtoken';

exports.userAuthent = async (req, res, next) => {
  if (req.isAuthenticated()) {
    return next();
  }
  res.send('Unauthorized access.');
};

exports.verifyToken = (req, res, next) => {
  try {
    const token = req.header('token');
    if (!token) {
      return res.status(401).send('Unauthorized access');
    }
    const verifiedUser = jwt.verify(token, 'secret');
    req.verifiedUser = verifiedUser;
    next();
  } catch (error) {
    res.status(403).send({ error: 'Invalid' });
  }
};

import Joi from '@hapi/joi';

exports.blogValidator = (req, res, next) => {
  const schema = Joi.object({
    title: Joi.string().trim().min(6).required(),
    content: Joi.string().trim().min(30).required(),
    imageUrl: Joi.string().trim().required(),
    date: Joi.date().iso(),
  });

  const result = schema.validate(req.body);
  if (result.error) {
    return res.status(400).json({
      status: 400,
      error: `${result.error.details[0].message}`,
    });
  }
  next();
};

